# Generate main script
generate_main_script() {
    local project_dir="$1"
    local project_name="$2"
    local template="$3"
    
    log_info "Generating main script..."
    
    local script_path="$project_dir/index.sh"
    
    cat > "$script_path" << 'EOF'
#!/usr/bin/env bash

# {{PROJECT_NAME}} - Main entry point
# Version: 0.1.0

set -euo pipefail

# Script directory
SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"

# Load configuration
if [[ -f "$SCRIPT_DIR/arty.yml" ]]; then
    # Configuration available via yq
    PROJECT_NAME=$(yq eval '.name' "$SCRIPT_DIR/arty.yml" 2>/dev/null || echo "{{PROJECT_NAME}}")
    PROJECT_VERSION=$(yq eval '.version' "$SCRIPT_DIR/arty.yml" 2>/dev/null || echo "0.1.0")
else
    PROJECT_NAME="{{PROJECT_NAME}}"
    PROJECT_VERSION="0.1.0"
fi

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m'

# Logging
log_info() { echo -e "${BLUE}[INFO]${NC} $1"; }
log_success() { echo -e "${GREEN}[OK]${NC} $1"; }
log_warn() { echo -e "${YELLOW}[WARN]${NC} $1"; }
log_error() { echo -e "${RED}[ERROR]${NC} $1" >&2; }

# Show usage
show_usage() {
    cat << USAGE
${PROJECT_NAME} v${PROJECT_VERSION}

USAGE:
    $(basename "$0") [options] [command]

OPTIONS:
    -h, --help      Show this help
    -v, --version   Show version

COMMANDS:
    start           Start the application
    test            Run tests
    help            Show this help

EXAMPLES:
    $(basename "$0") start
    $(basename "$0") --help
USAGE
}

# Main logic
main() {
    case "${1:-help}" in
        start)
            log_info "Starting ${PROJECT_NAME}..."
            # Your application logic here
            log_success "Running!"
            ;;
        test)
            log_info "Running tests..."
            bash "$SCRIPT_DIR/tests/run-tests.sh"
            ;;
        -h|--help|help)
            show_usage
            ;;
        -v|--version)
            echo "${PROJECT_NAME} v${PROJECT_VERSION}"
            ;;
        *)
            log_error "Unknown command: $1"
            show_usage
            exit 1
            ;;
    esac
}

# Run if executed directly
if [[ "${BASH_SOURCE[0]}" == "${0}" ]]; then
    main "$@"
fi
EOF

    # Replace template variables
    sed -i "s|{{PROJECT_NAME}}|${project_name}|g" "$script_path"
    chmod +x "$script_path"
    
    log_success "Generated main script: index.sh"
}

# Generate test framework
generate_tests() {
    local project_dir="$1"
    
    log_info "Generating test framework..."
    
    cat > "$project_dir/tests/run-tests.sh" << 'EOF'
#!/usr/bin/env bash

# Test runner

set -euo pipefail

TESTS_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
TEST_COUNT=0
PASS_COUNT=0
FAIL_COUNT=0

# Colors
GREEN='\033[0;32m'
RED='\033[0;31m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Test helpers
assert_equals() {
    TEST_COUNT=$((TEST_COUNT + 1))
    if [[ "$1" == "$2" ]]; then
        PASS_COUNT=$((PASS_COUNT + 1))
        echo -e "${GREEN}âœ“${NC} Test $TEST_COUNT passed"
        return 0
    else
        FAIL_COUNT=$((FAIL_COUNT + 1))
        echo -e "${RED}âœ—${NC} Test $TEST_COUNT failed"
        echo "  Expected: $2"
        echo "  Got: $1"
        return 1
    fi
}

assert_contains() {
    TEST_COUNT=$((TEST_COUNT + 1))
    if [[ "$1" == *"$2"* ]]; then
        PASS_COUNT=$((PASS_COUNT + 1))
        echo -e "${GREEN}âœ“${NC} Test $TEST_COUNT passed"
        return 0
    else
        FAIL_COUNT=$((FAIL_COUNT + 1))
        echo -e "${RED}âœ—${NC} Test $TEST_COUNT failed"
        echo "  String '$1' does not contain '$2'"
        return 1
    fi
}

# Run all test files
echo "Running tests..."
echo

for test_file in "$TESTS_DIR"/*_test.sh; do
    if [[ -f "$test_file" ]]; then
        echo "Running $(basename "$test_file")..."
        bash "$test_file"
        echo
    fi
done

# Summary
echo "================================"
echo "Test Summary:"
echo "  Total: $TEST_COUNT"
echo -e "  ${GREEN}Passed: $PASS_COUNT${NC}"
if [[ $FAIL_COUNT -gt 0 ]]; then
    echo -e "  ${RED}Failed: $FAIL_COUNT${NC}"
    exit 1
else
    echo -e "  ${GREEN}All tests passed!${NC}"
fi
EOF

    chmod +x "$project_dir/tests/run-tests.sh"
    
    # Create example test
    cat > "$project_dir/tests/example_test.sh" << 'EOF'
#!/usr/bin/env bash

# Example test file

source "$(dirname "${BASH_SOURCE[0]}")/run-tests.sh"

# Test cases
assert_equals "hello" "hello"
assert_contains "hello world" "world"

echo "Example tests completed"
EOF

    chmod +x "$project_dir/tests/example_test.sh"
    
    log_success "Generated test framework"
}

